es\/dwp\/themes\/custom\/cbs_metronic_theme\/contrib\/assets\/layouts\/layout\/css\/layout.css":1,"sites\/dwp\/themes\/custom\/cbs_metronic_theme\/contrib\/assets\/layouts\/layout\/css\/themes\/darkblue.css":1,"sites\/dwp\/themes\/custom\/cbs_metronic_theme\/contrib\/assets\/layouts\/layout\/css\/custom.css":1,"sites\/dwp\/themes\/custom\/cbs_metronic_theme\/css\/cbs-metronic-theme.styles.layout1.css":1,"sites\/dwp\/themes\/custom\/cbs_metronic_theme\/css\/ie.css":1,"sites\/dwp\/themes\/custom\/cbs_metronic_theme\/css\/print.css":1,"sites\/dwp\/themes\/custom\/cbs_dwp_metronic_theme\/css\/cbs-dwp-metronic-theme.styles.layout1.css":1,"0":1}},"cbs_wss_partner_info":{"blocks":{"donorStatsCard":{"lang":"en","gender":"UNKNOWN","cardTypes":["appointment_suggestions","bleed_time","blood_type","collection_volume","favourite_clinics","ferritin_level","hemoglobin_level","total_donations","websahh_qpass","onematch_eligible","onematch_registered","next_appointment_plasma_soon","next_appointment_plasma_today","next_appointment_platelets_soon","next_appointment_platelets_today","next_appointment_whole_blood_soon","next_appointment_whole_blood_today","next_eligible_plasma_deferred","next_eligible_plasma_soon","next_eligible_plasma_today","next_eligible_platelets_deferred","next_eligible_platelets_soon","next_eligible_platelets_today","next_eligible_whole_blood_deferred","next_eligible_whole_blood_soon","next_eligible_whole_blood_today"],"chartTypes":["hemoglobin_level","bleed_time","collection_volume","ferritin_level"],"dataPoints":[],"infoChunks":[],"chartsConfig":{"chartType":"spline","chartFontColour":null,"chartTitleFontColour":null,"chartSubTitleFontColour":null,"lineWidth":"4","seriesLineColour1":"#4c9c8f","seriesLineColour2":"#4c9c8f","yAxisTitleFontColour":"#ed1c24","yAxisLabelsFontColour":null,"yAxisLineWidth":"4","yAxisLineColour":"#8dd3d0","yAxisTickWidth":"4","yAxisTickColour":"#8dd3d0","yAxisGridLineWidth":null,"yAxisGridLineColour":null,"yAxisMinorGridLineWidth":null,"yAxisMinorGridLineColour":null,"xAxisTitleFontColour":"#ed1c24","xAxisLabelsFontColour":null,"xAxisLineWidth":"4","xAxisLineColour":"#8dd3d0","xAxisTickWidth":"4","xAxisTickColour":"#8dd3d0","xAxisGridLineWidth":null,"xAxisGridLineColour":null,"xAxisMinorGridLineWidth":null,"xAxisMinorGridLineColour":null,"tooltipHeaderTemplate":null,"tooltipHeaderFormat":null,"tooltipHeaderFontColour":null,"tooltipHeaderFontSize":null,"plotBandFontColour":null,"thresholdLineWidth":null,"thresholdLineColourLow":"#ff0000","thresholdLineColourNormal":null,"thresholdLineColourHigh":"#ff0000","thresholdFillColourLow":null,"thresholdFillColourNormal":null,"thresholdFillColourHigh":null},"tilt":{"enabled":false,"max":10,"scale":1.1000000000000001},"shake":{"enabled":true,"delayInitial":0,"delayRepeat":0},"stemCells":{"deferralItems":[{"key":"cancer","lang":"en","criteria":"Cancer (other than cured squamous or basal cell skin cancer or cervical in situ)","details":"\u003Cp\u003EThere is a risk of transmitting the malignancy (cancer) to the patient receiving the stem cell transplant; therefore, donors who have had or currently have this condition are deferred.\u003C\/p\u003E"},{"key":"stroke","lang":"en","criteria":"Stroke","details":"\u003Cp\u003EA very rare potential adverse reaction to stem cell donation is stroke. Therefore, donors with a history of having strokes are not eligible to donate stem cells in order to reduce the risk of a recurrent stroke.\u003C\/p\u003E"},{"key":"transplant","lang":"en","criteria":"Have you ever had an illness or condition requiring an organ transplant, stem cell transplant or dura mater (brain covering) graft?","details":"\u003Cp\u003EDonors who have received a transplant or dura mater graft may have medical conditions that make stem cell donation unsafe for either the donor or the recipient.\u003C\/p\u003E"},{"key":"heart_disease","lang":"en","criteria":"Heart disease or heart surgery","details":"\u003Cp\u003EDonors with a history of heart attacks or heart disease are at risk of complications during the donation process. These donors are ineligible to donate for reasons of donor safety.\u003C\/p\u003E"},{"key":"lung_disease","lang":"en","criteria":"Lung disease","details":"\u003Cp\u003EThe process of stem cell donation may be unsafe for donors with serious lung conditions.\u003C\/p\u003E"},{"key":"insulin","lang":"en","criteria":"Insulin-dependent diabetes","details":"\u003Cp\u003EDonors with this condition have increased risk of complications during the donation process; therefore, donors with this condition are not eligible to donate stem cells.\u003C\/p\u003E"},{"key":"colitis","lang":"en","criteria":"Ulcerative Colitis or Crohn\u0027s disease","details":"\u003Cp\u003EDonors with these conditions are at risk of medical complications during the donation process. These donors are ineligible to donate for reasons of donor safety.\u003C\/p\u003E"},{"key":"multiple_sclerosis","lang":"en","criteria":"Multiple Sclerosis","details":"\u003Cp\u003EThe process of stem cell donation may be unsafe for donors with mutiple sclerosis. There is also a theoretical risk that this condition may be passed from donor to stem cell transplant recipient.\u003C\/p\u003E"},{"key":"arthritis","lang":"en","criteria":"Rheumatoid Arthritis or Ankylosing Spondylitis","details":"\u003Cp\u003ESevere symptoms may be caused by the drug G-CSF which is a bone marrow stimulating agent administered prior to stem cell donation to help boost your body\u0027s production of blood stem cells into the bloodstream for collection. Because of this risk, donors with this condition are deferred.\u003C\/p\u003E"},{"key":"schizophrenia","lang":"en","criteria":"Schizophrenia","details":"\u003Cp\u003EThe process of stem cell donation may be unsafe for donors with schizophrenia.\u003C\/p\u003E"},{"key":"chagas","lang":"en","criteria":"Chagas\u0027 disease, Babesiosis or Leishmaniasis","details":"\u003Cp\u003EDiseases caused by the bite of infected insects may be transmissible by blood and therefore, could cause infection of the patient receving the stem cell transplant. Donors with these conditions are not eligible to donate stem cells.\u003C\/p\u003E"},{"key":"hemophilia","lang":"en","criteria":"Hemophilia","details":"\u003Cp\u003EThere is a risk of bleeding complications during the donation process and a risk of the bleeding disorder being passed to the patient; therefore, donors with this condition are deferred.\u003C\/p\u003E"},{"key":"hiv_aids","lang":"en","criteria":"AIDS\/HIV","details":"\u003Cp\u003EHIV is a disease transmissible by blood. This infection could be passed onto to the patient receving the stem cell transplant. Donors with this condition are not eligible to donate stem cells.\u003C\/p\u003E"},{"key":"anemia","lang":"en","criteria":"Aplastic anemia, sickle cell, genetic hemolitic anemia or thalassemia","details":"\u003Cp\u003EThe process of stem cell donation may be unsafe for donors with Aplastic anemia, sickle cell anemia, genetic hemolytic anemia, or thalassemia. These conditions may also be transmitted to the stem cell transplant recipient.\u003C\/p\u003E"}],"registrationStepsTitle":"\u003Cp\u003EYour registration steps\u003C\/p\u003E\n","registrationStepsContent":"\u003Col class=\u0022stem-cells-sequence-flow steps steps-registration\u0022\u003E\n  \u003Cli class=\u0022step step-1\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 1\u003C\/span\u003E\n        Check your eligibility\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EAnswer some questions to help us determine if you can join the stem cell registry.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-2\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 2\u003C\/span\u003E\n        Create your canadian Blood Services account\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003ECreate your account to save your contact information and keep it updated so we know how to reach you should you be identified as a match. If you already have an account, log in.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-3\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 3\u003C\/span\u003E\n        Confirm your email address\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003ELook for a verification email in your inbox to finish creating your account.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-4\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 4\u003C\/span\u003E\n        Log in to your account\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EEnter your new username and password.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-5\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 5\u003C\/span\u003E\n        Complete your stem cell registry profile\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EFill in your contact information and other required fields.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-6\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 6\u003C\/span\u003E\n        Provide consent\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003ERead and sign our consent form to complete your registration.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-7\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 7\u003C\/span\u003E\n        Receive your swab kit\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EWithin 1 to 3 weeks, you\u2019ll receive your buccal (cheek) swab kit in the mail. Please complete and return it promptly using the prepaid postage provided. We\u2019ll process your kit and notify you once you become officially searchable on the registry for patients in need.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n\u003C\/ol\u003E","patientMatchStepsTitle":"\u003Cp\u003EIf you\u0027re a match for a patient\u003C\/p\u003E\n","patientMatchStepsContent":"\u003Col class=\u0022stem-cells-sequence-flow steps steps-patient-match\u0022\u003E\n  \u003Cli class=\u0022step step-1\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 1\u003C\/span\u003E\n        Confirm your health\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EMonths or years may pass after you register to become a stem cell donor \u2013 we know health can change over time. Our comprehensive health screening will confirm you\u2019re still eligible to donate stem cells if you\u2019re selected as a match for a patient in need.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-2\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 2\u003C\/span\u003E\n        Learn donation details at your information session\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EA registered nurse will get in touch via your provided contact information and schedule a time for your information session, which will take place over the phone.\u003C\/p\u003E\n        \u003Cp\u003EThe information session will cover your donation method (PBSC or bone marrow), date, location, upcoming appointments, and other details leading up to and after your donation.\u003Cbr \/\u003E\n        \u003Ca href=\u0022https:\/\/blood.ca\/en\/stemcells\/donating-stemcells\/how-stemcell-donation-works\u0022\u003ELearn more about the information session\u003C\/a\u003E\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-3\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 3\u003C\/span\u003E\n        Take a blood test\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EOur team will arrange a visit for you to a nearby canadian Blood Services Centre where we\u2019ll collect your blood samples to test for infectious diseases.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-4\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 4\u003C\/span\u003E\n        Receive a physical examination\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EYour physical examination will be performed by a physician at the hospital where your donation will take place. The exam will cover routine medical tests to confirm your state of health. This ensures your donation will not be a risk to you or the patient.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-5\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 5\u003C\/span\u003E\n        Confirm your participation\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EOnce you\u2019ve been medically cleared, you\u2019ll be asked to sign a participation consent form before we notify the transplant centre. The patient will then begin their pre-transplant treatments (e.g. chemotherapy and\/or radiation).\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n  \u003Cli class=\u0022step step-6\u0022\u003E\n    \u003Cdiv class=\u0022step-inner\u0022\u003E\n      \u003Ch2\u003E\n        \u003Cspan class=\u0022icon\u0022\u003E\u003C\/span\u003E\n        \u003Cspan class=\u0022step\u0022\u003EStep 6\u003C\/span\u003E\n        Donate to the patient\n      \u003C\/h2\u003E\n      \u003Cdiv class=\u0022details\u0022\u003E\n        \u003Cp\u003EAfter your stem cells have been collected, they\u2019ll be transported to the transplant centre and infused into the patient.\u003C\/p\u003E\n      \u003C\/div\u003E\n    \u003C\/div\u003E\n  \u003C\/li\u003E\n\u003C\/ol\u003E\n\u003Cp\u003E\u003Ca href=\u0022https:\/\/blood.ca\/en\/stemcells\/donating-stemcells\/how-stemcell-donation-works\u0022 class=\u0022button full-width bold\u0022\u003ELearn more about the donation process\u003C\/a\u003E\u003C\/p\u003E","preScreenTitle":"\u003Cp\u003EJoin Our Stem Cell Registry\u003C\/p\u003E\n","preScreenTemplate":"\u003Cp\u003EAnswer a few questions to see if you\u2019re eligible.\u003C\/p\u003E\n\u003Cul class=\u0022bullets-hidden\u0022\u003E\n\u003Cli\u003E\u003Ca href=\u0022#\u0022 class=\u0022info-icon-link stem-cells-modal-popup-registration-steps\u0022\u003EYour registration steps\u003C\/a\u003E\u003C\/li\u003E\n\u003Cli\u003E\u003Ca href=\u0022#\u0022 class=\u0022info-icon-link stem-cells-modal-popup-patient-match-steps\u0022\u003EIf you\u2019re a match for a patient\u003C\/a\u003E\u003C\/li\u003E\n\u003C\/ul\u003E\n\u003Cdiv class=\u0022pre-screen-checklist-section\u0022\u003E\n  \u003Ch2\u003EHave you had, or do you have any of the following illnesses \/ conditions?\u003C\/h2\u003E\n  \u003Cp\u003ESome existing health conditions may risk your health and the health of the patient.\u003C\/p\u003E\n  \u003Cdiv class=\u0022pre-screen-checklist\u0022\u003E\u003C\/div\u003E\n\u003C\/div\u003E\n\u003Cp\u003EIf you have any questions, please contact canadian Blood Services at \u003Ca href=\u0022tel:1-888-236-6283\u0022\u003E1\u0026nbsp;888\u0026nbsp;2\u0026nbsp;DONATE\u003C\/a\u003E (1-888-236-6283).\u003C\/p\u003E","ineligibilityTitle":"\u003Cp\u003ESorry, You\u2019re Not Eligible To Join The Stem Cell Registry\u003C\/p\u003E\n","ineligibilityTemplate":"\u003Cp\u003ETo protect both donors and patients, there are certain age and health eligibility criteria that can prevent someone from joining canadian Blood Services Stem Cell Registry.\u003C\/p\u003E\n\u003Cp\u003EAccording to one or more of the answers you provided, you do not meet the age or medical requirements to join this registry. If you made a mistake in your questionnaire, please go back and update your answer(s).\u003C\/p\u003E\n\u003Cdiv class=\u0022ineligibility-reasons-section\u0022\u003E\n  \u003Ch2\u003EReason(s) why you don\u2019t qualify:\u003C\/h2\u003E\n  \u003Cdiv class=\u0022ineligibility-reasons\u0022\u003E\u003C\/div\u003E\n\u003C\/div\u003E","registrationPage":"onematch\/profile"}},"donorStatsCharts":{"lang":"en","gender":"UNKNOWN","chartTypes":["hemoglobin_level","bleed_time","collection_volume","ferritin_level"],"dataPoints":[],"infoChunks":[],"chartsConfig":{"chartType":"spline","chartFontColour":null,"chartTitleFontColour":null,"chartSubTitleFontColour":null,"lineWidth":"4","seriesLineColour1":"#4c9c8f","seriesLineColour2":"#cc0000","yAxisTitleFontColour":"#ed1c24","yAxisLabelsFontColour":null,"yAxisLineWidth":"4","yAxisLineColour":"#8dd3d0","yAxisTickWidth":"4","yAxisTickColour":"#8dd3d0","yAxisGridLineWidth":null,"yAxisGridLineColour":null,"yAxisMinorGridLineWidth":null,"yAxisMinorGridLineColour":null,"xAxisTitleFontColour":"#ed1c24","xAxisLabelsFontColour":null,"xAxisLineWidth":"4","xAxisLineColour":"#8dd3d0","xAxisTickWidth":"4","xAxisTickColour":"#8dd3d0","xAxisGridLineWidth":null,"xAxisGridLineColour":null,"xAxisMinorGridLineWidth":null,"xAxisMinorGridLineColour":null,"tooltipHeaderTemplate":null,"tooltipHeaderFormat":null,"tooltipHeaderFontColour":null,"tooltipHeaderFontSize":null,"plotBandFontColour":null,"thresholdLineWidth":null,"thresholdLineColourLow":"#ff0000","thresholdLineColourNormal":null,"thresholdLineColourHigh":"#ff0000","thresholdFillColourLow":null,"thresholdFillColourNormal":null,"thresholdFillColourHigh":null},"tilt":{"enabled":false,"max":10,"scale":1.1000000000000001}}}},"cbs_wss_eligibility":{"blocks":{"eligibilityChecklist":{"eligibility":{"criteria":[{"id":"94","lang":"en","image":"","requirement":"\u003Cp\u003E\u003Cstrong\u003ECan I donate blood or plasma?\u0